mapper.select:
SELECT b.*, v.guestHouseName, v.roomName FROM booking_tb b,  bkrmghmatching_view v 
WHERE b.bookingCode=v.bookingCode AND v.hostCode=#{hostCode} 
ORDER BY b.bookingCode desc
LIMIT #{cri.pageStart}, #{cri.perPageNum}

SELECT count(*) FROM booking_tb b,  bkrmghmatching_view v 
WHERE b.bookingCode=v.bookingCode AND v.hostCode=#{hostCode} 
ORDER BY b.bookingCode desc
